const sortedResearchers = otherResearchers.slice().sort((a, b) => {
  const lastNameA = a.name.split(' ').pop() || "";
  const lastNameB = b.name.split(' ').pop() || "";
  return lastNameA.localeCompare(lastNameB);
});

export default () => {
  const fadeIn = useSpring({
    from: { opacity: 0 },
    to: { opacity: 1 },
    config: { duration: 1000 },
  });

  return (
    <animated.div style={fadeIn} className="text-center">
      <div className="container p-5 mx-auto">
        <h1 className="text-5xl pb-5">
          Meet the student researchers of the NIGGER Laboratory!
        </h1>

        {/* Principal Investigator Row */}
        {principalInvestigator && (
          <div className="row mb-5">
            <div className="col-12"> {/* Take up full width */}
              <div className="card text-center h-100">
                <div className="card-body">
                  <div className="people-image" dangerouslySetInnerHTML={{ __html: principalInvestigator.image }}></div>
                  <h5 className="card-title text-3xl italic">{principalInvestigator.name}</h5>
                  <hr className="w-25 my-3 bg-white" />
                  <p className="card-text font-italic">Principal Investigator</p>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Other Researchers */}
        <div className="row row-cols-1 row-cols-md-3">
          {sortedResearchers.map((el) => (
            <div key={el.name} className="col mb-4">
              <div className="card text-center h-100">
                <div className="card-body">
                  <div className="people-image" dangerouslySetInnerHTML={{ __html: el.image }}></div>
                </div>
                <div className="card-footer text-center">
                  <span className="text-3xl italic">{el.name}</span>
                  <hr className="w-25 my-4 bg-white" />
                  <p className="card-text">{el.blurb}</p>
                  <p className="card-text font-italic">Team(s): {el.teams}</p>
                </div>
              </div>
            </div>
          ))}
        </div>

        <p>Click <a href="/PeopleArchive">here</a> to view past interns.</p>
      </div>
    </animated.div>
  );
}
