import React from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';
import { useSpring, animated } from 'react-spring';
import { AiOutlineExperiment } from 'react-icons/ai'; // Import an experiment icon
import 'bootstrap/dist/css/bootstrap.min.css';

const Biosensing = () => {
    const fadeIn = useSpring({
        from: { opacity: 0 },
        to: { opacity: 1 },
        config: { duration: 1000 },
    });

    return (
        <animated.div className="cover-container d-flex w-100 h-100 p-3 mx-auto flex-column" style={fadeIn}>
            <main className="px-3">
                <h1>Biosensing Innovations</h1>
                <p className="lead">
                    Explore the cutting-edge biosensing technologies developed in our nanotechnology lab.
                </p>

                <Row>
                    {/* Card 1 */}
                    <Col md={6} className="mb-4">
                        <Card className="h-100">
                            <Card.Img variant="top" src="https://via.placeholder.com/600x300" className="smaller-image" alt="Card 1" />
                            <Card.Body>
                                <AiOutlineExperiment size={30} className="mb-2 text-muted" />
                                <Card.Title className="bigger-text">Biosensing Experiment 1</Card.Title>
                                <Card.Text className="bigger-text">
                                    Description of the first aspect of the biosensing project. Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </Col>

                    {/* Card 2 */}
                    <Col md={6} className="mb-4">
                        <Card className="h-100">
                            <Card.Img variant="top" src="https://via.placeholder.com/600x300" className="smaller-image" alt="Card 2" />
                            <Card.Body>
                                <AiOutlineExperiment size={30} className="mb-2 text-muted" />
                                <Card.Title className="bigger-text">Biosensing Experiment 2</Card.Title>
                                <Card.Text className="bigger-text">
                                    Description of the second aspect of the biosensing project. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </Col>

                    {/* ... (rest of the cards remain the same) */}
                </Row>
            </main>

            <footer className="mt-auto">
                <p>Â© 2024 Nanotechnology Lab at UC Berkeley</p>
            </footer>
        </animated.div>
    );
};

export default Biosensing;
