import React, { useEffect, useState } from 'react';
import { useSpring, animated } from 'react-spring';
import { useNavigate } from 'react-router-dom';
import { decodeToken } from 'react-jwt';

const Biosensor = () => {
    const fadeIn = useSpring({
        from: { opacity: 0 },
        to: { opacity: 1 },
        config: { duration: 1000 },
    });
    const navigate = useNavigate();
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const token = sessionStorage.getItem('token');
                if (!token) {
                    navigate('/login');
                    return;
                }
                const user = decodeToken(token);
                if (!user) {
                    sessionStorage.removeItem('token');
                    navigate('/login');
                    return;
                }
                setLoading(false);
                const req = await fetch('http://localhost:1337/api/Biosensor', {
                    headers: {
                        'x-access-token': token,
                    },
                });
                const data = await req.json();
                console.log(data);
            } catch (error) {
                console.error('Error fetching data:', error);
                sessionStorage.removeItem('token');
                navigate('/login');
            }
        };
        fetchData();
        return () => {
            // Any cleanup logic (e.g., canceling requests, unsubscribing from subscriptions)
        };
    }, [navigate]);

    // Render loading state
    if (loading) {
        return <p>Loading...</p>;
    }
    return (
        <animated.div className="cover-container d-flex w-100 h-100 p-3 mx-auto flex-column" style={fadeIn}>
            <h1>Biosensor page under construction!</h1>
        </animated.div>
    );
};

export default Biosensor;
