import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';

const EnergyStorageCard = ({ backgroundImage, title, content, buttonText }) => {
  const [isHovered, setIsHovered] = useState(false);

  const cardSpring = useSpring({
    scale: isHovered ? 1.05 : 1,
  });

  return (
    <animated.div
      className="card card-cover h-100 overflow-hidden text-white bg-dark rounded-5 shadow-lg"
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        transform: cardSpring.scale.interpolate(scale => `scale(${scale})`),
        border: "2px solid black", // Add this line for black border
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="d-flex flex-column h-100 p-5 pb-3 text-white text-shadow-1">
        <h2 className="pt-5 mt-5 mb-4 display-6 lh-1 fw-bold">{title}</h2>
        {content && <p>{content}</p>}
        {buttonText && (
          <div className="d-grid gap-2 d-md-flex justify-content-md-start mb-4 mb-lg-3">
            <button type="button" className="btn btn-success btn-lg px-4 me-md-2 fw-bold">{buttonText}</button>
          </div>
        )}
      </div>
    </animated.div>
  );
};

const EnergyStorage = () => {
  return (
    <div className="energy-storage-page">
      {/* ... (existing code) ... */}
      <div className="container px-4 py-5" id="energyStorageProjects">
        <h2 className="pb-2 border-bottom">Energy Storage Projects</h2>

        <div className="row row-cols-1 row-cols-lg-3 align-items-stretch g-4 py-5">
          {/* ... (existing cards) ... */}
          <div className="col">
            <EnergyStorageCard
              backgroundImage="your_image_path_1.jpg"
              title="Project Title 1"
              content="Project content goes here."
              buttonText="Learn More"
            />
          </div>

          <div className="col">
            <EnergyStorageCard
              backgroundImage="your_image_path_2.jpg"
              title="Project Title 2"
              content="Project content goes here."
              buttonText="Explore"
            />
          </div>

          <div className="col">
            <EnergyStorageCard
              backgroundImage="your_image_path_3.jpg"
              title="Project Title 3"
              content="Project content goes here."
              buttonText="See Details"
            />
          </div>
        </div>
        <footer className="mt-auto">
          <p>Â© 2024 Nanotechnology Lab at UC Berkeley</p>
        </footer>
      </div>
    </div>
  );
};

export default EnergyStorage;
