import React from 'react';

const PeoplePage = () => {
  const people = [
    // ... (your people data array)
  ];

  const cardStyle = {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
  };

  const personCardStyle = {
    flexBasis: 'calc(33.33% - 30px)', // Adjust the width of each card as needed
    margin: '15px',
    padding: '15px',
    border: '1px solid #ccc',
    textAlign: 'left',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
  };

  const imageStyle = {
    width: '250px',
    maxHeight: '200px', // Adjust the maximum height as needed
    objectFit: 'cover', // Preserve aspect ratio
  };

  return (
    <div style={cardStyle}>
      {people.map((person, index) => (
        <div key={index} style={personCardStyle}>
          <div>
            <a href={person.image} target="_blank" rel="noopener noreferrer">
              <img src={person.image} alt={person.name} style={imageStyle} />
            </a>
          </div>
          <div>
            <h2>{person.name}</h2>
            <p>{person.teams}</p>
            <p dangerouslySetInnerHTML={{ __html: person.blurb }} />
          </div>
        </div>
      ))}
    </div>
  );
};

export default PeoplePage;
