import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Login.css';

const Login = () => {
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = (e) => {
    e.preventDefault(); // Prevent the form from submitting

    // Add your authentication logic here (e.g., check password against a server)
    // For simplicity, let's just check if the password is not empty
    if (password) {
      setIsLoggedIn(true);
      // You can navigate to another page or perform additional actions upon successful login
    } else {
      alert('Password cannot be empty');
    }
  };

  return (
    <body className="d-flex align-items-center py-4">
      <main className="form-signin w-100 m-auto">
        <form onSubmit={handleLogin}>
          <img className="mb-4" src="/docs/5.3/assets/brand/bootstrap-logo.svg" alt="" width="72" height="57" />
          <h1 className="h3 mb-3 fw-normal">Please sign in</h1>

          <div className="form-floating">
            <input
              type="password"
              className="form-control"
              id="floatingPassword"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <label htmlFor="floatingPassword">Password</label>
          </div>

          <button className="btn btn-primary w-100 py-2 loginbutton" type="submit">
            Sign in
          </button>
        </form>
      </main>
      {/* Note: Remove the bootstrap script if you have already included it in your project */}
      <script src="/docs/5.3/dist/js/bootstrap.bundle.min.js" integrity="sha384-C6RzsynM9kWDrMNeT87bh95OGNyZPhcTNXj1NW7RuBCsyN/o0jlpcV8Qyq46cDfL" crossorigin="anonymous"></script>
    </body>
  );
};

export default Login;
